import React, { Component } from "react";
import Disarm from "./Buttons/Disarm";
import Arm from "./Buttons/Arm";
import { Joystick } from "react-joystick-component";
import { Row, Col, Container } from "react-bootstrap";

class Teleoperation extends Component {
  state = { isArmed: "bruh" };

  constructor() {
    super();
    this.init_connection();
  }

  handleMove() {}
  handleStop() {}

  componentDidMount() {
    this.getDroneState();
  }

  getDroneState() {
    // create a pose subscriber
    var state_subscriber = new window.ROSLIB.Topic({
      ros: this.state.ros,
      name: "/mavros/state",
      messageType: "mavros_msgs/State",
    });

    state_subscriber.subscribe((message) => {
      this.setState({ isArmed: message.armed });
    });
  }
  render() {
    return (
      <div>
        <h2>Teleoperation</h2>
        <Container>
          <Row className="justify-content-md-center">
            <Col>
              {" "}
              <Joystick
                size={80}
                sticky={false}
                baseColor="#EEEEEE"
                stickColor="#BBBBBB"
                move={this.handleMove}
                stop={this.handleStop}
              ></Joystick>
            </Col>
          </Row>
          <Row>{this.state.isArmed ? <Disarm></Disarm> : <Arm></Arm>}</Row>
        </Container>
      </div>
    );
  }
}

export default Teleoperation;
