import React, { Component } from "react";
import { Alert } from "react-bootstrap";

class Connection extends Component {
  state = { connected: true, ros: null };

  constructor() {
    super();
    this.init_connection();
  }

  init_connection() {
    this.state.ros = new window.ROSLIB.Ros(); // Using window bc using CDN for ROSLIB and not NPM package
    console.log(this.state.ros);

    this.state.ros.on("connection", () => {
      console.log("Connection established.");
      this.setState({ connected: true });
    });

    this.state.ros.on("close", () => {
      console.log("Connection closed.");
      this.setState({ connected: false });
    });

    this.state.ros.connect();
  }

  render() {
    return (
      <Alert
        className="text-center my-4"
        variant={this.state.connected ? "success" : "danger"}
      >
        {this.state.connected ? "Drone Connected" : "Drone Disconnected"}
      </Alert>
    );
  }
}

export default Connection;
