import React, { Component } from "react";
import { FormControl } from "react-bootstrap";
import { InputGroup } from "react-bootstrap";
import Go from "./Buttons/Go";
import { Row, Col, Container } from "react-bootstrap";
import { Form } from "react-bootstrap";
import Config from "../scripts/config";

class Coordinates extends Component {
  state = { ros: null, checked: false };

  constructor() {
    super();
    this.init_connection();
  }

  init_connection() {
    this.state.ros = new window.ROSLIB.Ros(); // Using window bc using CDN for ROSLIB and not NPM package

    // Changing changing button color according to connection state
    this.state.ros.on("connection", () => {
      this.setState({ connected: true });
    });

    this.state.ros.on("close", () => {
      this.setState({ connected: false });
      //try to reconnect every 2 seconds
      setTimeout(() => {
        try {
          this.state.ros.connect(
            "ws://" +
              Config.ROSBRIDGE_SERVER_IP +
              ":" +
              Config.ROSBRIDGE_SERVER_PORT +
              ""
          );
        } catch (error) {
          console.log("connection problem");
        }
      }, Config.RECONNECTION_TIMER);
    });

    try {
      this.state.ros.connect(
        "ws://" +
          Config.ROSBRIDGE_SERVER_IP +
          ":" +
          Config.ROSBRIDGE_SERVER_PORT +
          ""
      );
    } catch (error) {
      console.log("connection problem");
    }
  }

  render() {
    return (
      <div className="d-grid">
        {" "}
        <Row>
          <Col>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="X"
                aria-label="X"
                aria-describedby="basic-addon1"
                type="number"
              />
            </InputGroup>
          </Col>
          <Col>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Y"
                aria-label="Y"
                aria-describedby="basic-addon1"
                type="number"
              />
            </InputGroup>
          </Col>
          <Col>
            <InputGroup className="mb-3">
              <FormControl
                placeholder="Z"
                aria-label="Z"
                aria-describedby="basic-addon1"
                type="number"
              />
            </InputGroup>
          </Col>
          <Col>
            <Form.Check type="switch" id="custom-switch" />
          </Col>
        </Row>
        <Go></Go>
      </div>
    );
  }
}

export default Coordinates;
